import { useRef } from "react";
import { useOverlayScrollbarTarget } from "@underverse-ui/underverse";

export function LogPanel(){
  const ref = useRef<HTMLDivElement>(null);
  useOverlayScrollbarTarget(ref, { enabled: true });

  return <div ref={ref} className="h-64 overflow-y-auto">...</div>;
}
